	/**根据地名取得区号
	 * 
	 * @param strCity
	 * @return
	 */
	public String GetCityArea(String strCity){
		String key, val,strResult="";
        Iterator it = pctMapInfo.entrySet().iterator();
        try {
			while (it.hasNext()) {
			     Map.Entry pairs = (Map.Entry) it.next();
			     key = pairs.getKey().toString();
			     val = pairs.getValue().toString();
			     //判断 是否包含此城市
			     int result=key.indexOf(strCity);
			     if(result>=0){
			    	 strResult=val;
			    	 break;
			     }
			 }
		} catch (Exception e) {
			// TODO Auto-generated catch block
			//strResult="Error:"+e.getMessage().toString();
			e.printStackTrace();
		}
        
		return strResult;
		
	}
